INSERT INTO site_news
   (`id`, `title`, `image`, `short_text`, `full_text`, `type`, `name`, `date`)
VALUES
   (3, 'Юбилей школы', '', '25 февраля нашему лицею исполнилось 50 лет со дня основания! Состоялись торжественные празднования, а также встречи выпускников прошлых лет', '25 февраля провели концерт посвященный «50-летнему Юбилею школы №7».<br>\r\nОрганизовали чаепитие для ветеранов. 54 педагога лицея награждены  Грамотами  и  Благодарностями  Губернатора , Законодательного собрания, Министерства образования, науки и инновационной политики  Новосибирской области, главы города Бердска, управления образования.\r\n<br><br> \r\n26 февраля провели концерт посвященный «50-летнему Юбилею школы №7», для выпускников школы.<br>\r\nБолее 400 выпускников   присутствовали на  вечере встречи выпускников. В выступлениях  выпускников   звучали слова благодарности педагогам лицея за особую атмосферу, доброжелательность, чувство семьи, создаваемое ими.\r\n<br><br>\r\nВ приказе №303  Министерства образования, науки и инновационной политики, утверждены списки призеров и победителей регионального этапа олимпиады. 10 обучающихся лицея стали призерами и победителями  регионального этапа олимпиады школьников.\r\n<br><br>\r\nФотоотчёт о концерте к юбилею лицея  вы можете посмотреть в <a href=?s=photos&album=5>Галерее</a>\r\n', 0, 'Админ', "26.2.2011 10:00:00");
INSERT INTO site_news
