VALUES
   (1, 'Приветствие!', '', 'Добро пожаловать на сайт нашего лицея №7! Надеемся, что вам будет здесь уютно и комфортно! Располагайтесь!', 'Добро пожаловать на сайт нашего лицея №7! Надеемся, что вам будет здесь уютно и комфортно! Располагайтесь!', 0, 'Админ', "1.2.2011 11:10:20");
INSERT INTO site_news
   (`id`, `title`, `image`, `short_text`, `full_text`, `type`, `name`, `date`)
VALUES
   (2, 'Приезд Александра Карелина', '', 'Наш лицей посетил «Карелин Фонд» - олимпийские чемпионы: Карелин А.А., Подгорный Е.А., Глухих А.В., Минх И.Э. ', '14 февраля наш лицей посетил «Карелин Фонд» - олимпийские чемпионы: Карелин А.А., Подгорный Е.А., Глухих А.В., Минх И.Э. В спортивном зале с 10 до 11 часов прошла спортивная эстафета «Будь верен России!» для 3,6 классов. \r\n<br><br>С 11 до 12 часов в актовом зале прошла встреча с олимпийскими звездами у 4в, 6, 9-11 классов. На конференции ребятам представилась возможность задать вопрос чемпиону и получить на него ответ.  \r\n<br><br>\r\nПриезд чемпионов не остался незамеченным. Фотоотчёт вы можете посмотреть в <a href=?s=photos&album=4>Галерее</a>', 0, 'Админ', "14.2.2011 21:41:01");
